mapper.select:
SELECT a.store_id, a.store_name, a.order_index, b.book_id, b.book_name, b.book_name_en, c.cata_id, c.cata_name, c.cata_name_en, e.author_id, e.author_name, e.author_name_en, a.create_time, b.is_completion FROM store a, book b, catagory c, cata_book_relation d, author e WHERE d.book_id = b.book_id AND d.cata_id = c.cata_id AND b.last_store_id = a.store_id AND b.author_id = e.author_id
AND c.cata_name_en =#{cataNameEn,jdbcType=VARCHAR}
AND b.is_completion =#{isCompletion,jdbcType=INTEGER}
ORDER BY a.create_time DESC
ORDER BY b.create_time DESC

select 
a.id, a.book_id, book_name, book_name_en, book_desc, a.create_time, a.update_time, is_completion, 
author_id, hits, image_url, author_name,author_name_en,c.cata_name,c.cata_name_en  
FROM book a,cata_book_relation b,catagory c WHERE b.book_id=a.book_id AND b.cata_id=c.cata_id  

    and a.book_name = #{bookName,jdbcType=VARCHAR}


    and a.book_name_en = #{bookNameEn,jdbcType=VARCHAR}


    and a.author_name = #{authorName,jdbcType=VARCHAR}


    and a.author_name_en = #{authorNameEn,jdbcType=VARCHAR}

mapper.insert:
insert into book (id, book_id, book_name, 
  book_name_en, book_desc, create_time, 
  update_time, is_completion, author_id, 
  hits, image_url, author_name,author_name_en,last_store_id
  )
values (#{id,jdbcType=VARCHAR}, #{bookId,jdbcType=VARCHAR}, #{bookName,jdbcType=VARCHAR}, 
  #{bookNameEn,jdbcType=VARCHAR}, #{bookDesc,jdbcType=VARCHAR}, #{createTime,jdbcType=DATE}, 
  #{updateTime,jdbcType=DATE}, #{isCompletion,jdbcType=INTEGER}, #{authorId,jdbcType=VARCHAR}, 
  #{hits,jdbcType=BIGINT}, #{imageUrl,jdbcType=VARCHAR}, #{authorName,jdbcType=VARCHAR},#{authorNameEn,jdbcType=VARCHAR},#{lastStoreId,jdbcType=VARCHAR}
  )
